mapper.update:
update

set
id=#{id}
, shop_basic_id=#{shopBasicId}
, shop_name=#{shopName}
, company_homepage=#{companyHomepage}
, company_type=#{companyType}
, business_registration_number=#{businessRegistrationNumber}
, unified_social_credit_code=#{unifiedSocialCreditCode}
, registered_address=#{registeredAddress}
, registered_capital=#{registeredCapital}
, establishment_date=#{establishmentDate}
, business_period_begins=#{businessPeriodBegins}
, business_period_end=#{businessPeriodEnd}
, fixed_telephone=#{fixedTelephone}
, legal_representative=#{legalRepresentative}
, business_license_url=#{businessLicenseUrl}
WHERE
id=#{id}

mapper.select:
select sc.shop_basic_id,sc.shop_name,sc.company_homepage,sc.company_type,sc.business_registration_number,sc.unified_social_credit_code,
sc.registered_address,sc.registered_capital,sc.establishment_date,sc.business_period_begins,sc.business_period_end,sc.fixed_telephone,
sc.legal_representative,sc.business_license_url,sc.id AS sc_id
 ,sa.shop_complete_id,sa.application_person,sa.application_time,
    sa.application_description,sa.audit_status,sa.audit_opinion,sa.audit_time,sa.audit_result,sa.id AS sa_id
from
 AS sc LEFT JOIN t_shop_audit AS sa ON sc.id = sa.shop_complete_id

    AND shop_basic_id=#{shopBasicId}
    AND shop_name=#{shopName}
    AND company_homepage=#{companyHomepage}
    AND company_type=#{companyType}
    AND business_registration_number=#{businessRegistrationNumber}
    AND unified_social_credit_code=#{unifiedSocialCreditCode}
    AND registered_address=#{registeredAddress}
    AND registered_capital=#{registeredCapital}
    AND establishment_date=#{establishmentDate}
    AND business_period_begins=#{businessPeriodBegins}
    AND business_period_end=#{businessPeriodEnd}
    AND fixed_telephone=#{fixedTelephone}
    AND legal_representative=#{legalRepresentative}
    AND business_license_url=#{businessLicenseUrl}
    AND sc_id=#{id}

select sc.shop_basic_id,sc.shop_name,sc.company_homepage,sc.company_type,sc.business_registration_number,sc.unified_social_credit_code,
sc.registered_address,sc.registered_capital,sc.establishment_date,sc.business_period_begins,sc.business_period_end,sc.fixed_telephone,
sc.legal_representative,sc.business_license_url,sc.id AS sc_id
,sa.shop_complete_id,sa.application_person,sa.application_time,
sa.application_description,sa.audit_status,sa.audit_opinion,sa.audit_time,sa.audit_result,sa.id AS sa_id
from
 AS sc LEFT JOIN t_shop_audit AS sa ON sc.id = sa.shop_complete_id

    AND shop_basic_id=#{pd.entity.shopBasicId}
    AND shop_name=#{pd.entity.shopName}
    AND company_homepage=#{pd.entity.companyHomepage}
    AND company_type=#{pd.companyType}
    AND business_registration_number=#{pd.entity.businessRegistrationNumber}
    AND unified_social_credit_code=#{pd.entity.unifiedSocialCreditCode}
    AND registered_address=#{pd.entity.registeredAddress}
    AND registered_capital=#{pd.entity.registeredCapital}
    AND establishment_date=#{pd.entity.establishmentDate}
    AND business_period_begins=#{pd.entity.businessPeriodBegins}
    AND business_period_end=#{pd.entity.businessPeriodEnd}
    AND fixed_telephone=#{pd.entity.fixedTelephone}
    AND legal_representative=#{pd.entity.legalRepresentative}
    AND business_license_url=#{pd.entity.businessLicenseUrl}
    AND sc_id=#{pd.entity.id}
